Str.FuncTxt:
local str = "None"
                    local abil = session.GetAbilityByName('Illusionist22');
                    if abil ~= nil then
                        local abilObj = GetIES(abil:GetObject());
                        if abilObj.ActiveState == 1 then
                            str = "Illusionist22"
                            return str;
                        end
                    end
                    local abil_2 = session.GetAbilityByName('Illusionist23');
                    if abil_2 ~= nil then
                        local abilObj = GetIES(abil_2:GetObject());
                        if abilObj.ActiveState == 1 then
                            str = "Illusionist23"
                            return str;
                        end
                    end
                    return str
